import"../brown-style.js";const brownTabStyle=document.createElement("dom-module");brownTabStyle.innerHTML=`<template>
  <style include="brown-style">
    :host {
        @apply --layout-vertical;
    }

    :host > #header {/*v*/
      @apply --layout-flex-none;
      @apply --layout-self-stretch;
      @apply --layout-horizontal;
      @apply --layout-center;
      background-color: var(--tab-header-bgcolor);
      min-height: var(--tab-header-minheight);
      padding-left:10px;
    }

    :host([dynamic-tab]) > #header {
      padding-left:40px;
    }

    :host > #header > .scroller {/*v*/
      @apply --layout-flex-none;
      height:16px;
      color: var(--tabitem-active-color);
      cursor:pointer;
      visibility:hidden;
    }

    :host > #header > .scroller.scroll {/*v*/
      visibility:visible;
    }

    :host > #header > #header-item {/*v*/
      @apply --layout-flex;
      @apply --layout-self-stretch;
      @apply --layout-horizontal;
      position:relative;
      overflow-x:hidden;
    }

    :host > #header > #header-item > ::slotted([slot=header]) {
      @apply --layout-flex-none;
      @apply --layout-self-stretch;
      padding: 0px 10px;
    }

    :host > #content {
      @apply --layout-flex;
      @apply --layout-self-stretch;
      position:relative;
      background-color: var(--tab-content-bgcolor);
    }

    :host > #content > ::slotted([slot=content]) {
      @apply --layout-fit;
      margin:5px 5px;
      font-family:var(--tab-content-font-family);
    }
  </style>
</template>`;brownTabStyle.register("brown-tab-style");