import {html, PolymerElement} from '@polymer/polymer/polymer-element.js';

class BlackField extends PolymerElement {
  static get template() {
    return html`
      <style>
        :host {
          display:none;
        }
      </style>
    `;
  }

  static get properties() {
      return {
          key: {
              type:String
          },
          value: {
              type:String
          }
      };
  }

  ready() {
      super.ready();
      this.setAttribute("slot", "field");
  }
}

window.customElements.define('black-field', BlackField);
