import {html, PolymerElement} from '@polymer/polymer/polymer-element.js';
import '@webcomponents/shadycss/entrypoints/apply-shim.js';
import {BeruangBehavior} from '@beruang/beruang-behavior.js';
import {BrownBehavior} from '@beruang/brown/brown-behavior.js';
import '@polymer/iron-flex-layout/iron-flex-layout.js';
import '@polymer/polymer/lib/elements/dom-if.js';
import '@beruang/brown/brown-style.js';

class BrownMask extends BrownBehavior(BeruangBehavior(PolymerElement)) {
    static get template() {
        return html`
            <style  include="brown-style">
                :host {
                    @apply --layout-horizontal;
                    @apply --layout-center-center;
                    background-color:var(--mask-background-color);
                    z-index:999999;
                }

                :host > div {
                    background-color:var(--mask-message-background-color);
                    color:var(--mask-message-color);
                    font-size:var(--mask-message-font-size);
                    font-family:var(--mask-message-font-family);
                    padding:30px 60px;
                }
            </style>
            <template is="dom-if" if="[[_string(message)]]">
                <div>[[message]]</div>
            </template>
        `;
    }

    static get properties() {
      return {
          message:{
              type:String
          }
      };
    }

    ready() {
        super.ready();
        this.setAttribute("slot", "mask");
        this.setAttribute("fit-parent", true);
    }

    _string(s) {
        return s && s.length>0;
    }

}

window.customElements.define('brown-mask', BrownMask);
