import '../beruang-style.js';
import '@beruang/theme/beruang-theme.js';

const brownStyle = document.createElement('dom-module');
brownStyle.innerHTML =
`<template>
  <style include="beruang-style">
  :host {
    --nouserselect: {
      -webkit-user-select: none; /* Safari 3.1+ */
      -moz-user-select: none; /* Firefox 2+ */
      -ms-user-select: none; /* IE 10+ */
      user-select: none; /* Standard syntax */
    }
  }
  </style>
</template>`;
brownStyle.register('brown-style');
