import {html, PolymerElement} from '@polymer/polymer/polymer-element.js';
import '@webcomponents/shadycss/entrypoints/apply-shim.js';
import {BeruangBehavior} from '@beruang/beruang-behavior.js';
import {BrownBehavior} from '@beruang/brown/brown-behavior.js';
import '@beruang/brown/brown-style.js';

class BrownTabDesktop extends BrownBehavior(BeruangBehavior(PolymerElement)) {
  static get template() {
    return html`
      <style  include="brown-style">
        :host {
          @apply --layout-vertical;
        }

        :host > #header {
          @apply --layout-horizontal;
          @apply --layout-flex-none;
          @apply --layout-self-stretch;
          background-color: var(--tab-header-bgcolor);
          color: var(--tab-header-color);
          height: var(--tab-header-height);
          padding-left:10px;
        }

        :host([dynamic]) >#header {
          padding-left:40px;
        }

        :host > #header ::slotted([slot=header]) {
          font-family: var(--tab-header-font-family);
          font-size: var(--tab-header-font-size);
          line-height: var(--tab-header-height);
          padding: 0px 10px;
        }

        :host > #content {
          @apply --layout-flex;
          @apply --layout-self-stretch;
          background-color: var(--tab-content-bgcolor);
        }
      </style>
      <div id="header"><slot name="header"></slot></div>
      <div id="content"><slot name="content"></slot></div>
    `;
  }

  static get properties() {
    return {
      dynamic: {
        type:Boolean,
        value:false,
        reflectToAttribute:true
      }
    };
  }
}

window.customElements.define('brown-tab-desktop', BrownTabDesktop);
