import {html, PolymerElement} from '@polymer/polymer/polymer-element.js';
import '@polymer/iron-flex-layout/iron-flex-layout.js';
import {BeruangBehavior} from '@beruang/beruang-behavior.js';
import {PolarBehavior} from '@beruang/polar/polar-behavior.js';
import '@beruang/polar/polar-style.js';

/**
`polar-col` is vertical layout manager.
@customElement
@polymer
*/
class PolarBorder extends PolarBehavior(BeruangBehavior(PolymerElement)) {
    static get template() {
        return html`
            <style  include="polar-style">
                 :host{
                     position: absolute;
                     width: 100%;
                     height:100%;
                     top: 0px;
                     right: 0px;
                     bottom: 0px;
                     left: 0px;
                     background-color: red;
                     @apply --layout-vertical;
                 }

                 :host > .north {
                     @apply --layout-flex-none;
                     @apply --layout-self-stretch;
                     height: 100px;
                     background-color: red;
                 }

                 :host > .mid {
                     @apply --layout-flex-auto;
                     @apply --layout-self-stretch;
                     @apply --layout-horizontal;
                     background-color: yellow;
                 }

                 :host > .south {
                     @apply --layout-flex-none;
                     @apply --layout-self-stretch;
                     height: 100px;
                     background-color: blue;
                 }

                 :host > .mid > .west {
                     @apply --layout-flex-none;
                     @apply --layout-self-stretch;
                     width: 100px;
                     background-color: cyan;
                 }

                 :host > .mid >  .center {
                     @apply --layout-flex-auto;
                     @apply --layout-self-stretch;
                     background-color: yellow;
                 }

                 :host > .mid > .east {
                     @apply --layout-flex-none;
                     @apply --layout-self-stretch;
                     width: 100px;
                     background-color: magenta;
                 }
            </style>
            <div class="north"></div>
	        <div class="mid">
        		<div class="west"></div>
        		<div class="center"></div>
        		<div class="east"></div>
            </div>
            <div class="south"></div>
        `;
    }

    static get properties() {
      return {
          north:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          },

          mid:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          },

          south:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          },

          west:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          },

          center:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          },

          east:{
              type: Boolean,
              value: false,
              //reflectToAttribute: true
          }
      };
    }
}
window.customElements.define('polar-border', PolarBorder);
