import {html, PolymerElement} from '@polymer/polymer/polymer-element.js';
import '@polymer/iron-flex-layout/iron-flex-layout.js';
import {BeruangBehavior} from '@beruang/beruang-behavior.js';
import {PolarBehavior} from '@beruang/polar/polar-behavior.js';
import {PolarSimpleBehavior} from './polar-simple-behavior.js';
import './polar-simple-style.js';

/**
`polar-col` is vertical layout manager.
@customElement
@polymer
*/
class PolarCol extends PolarSimpleBehavior(PolarBehavior(BeruangBehavior(PolymerElement))) {
  static get template() {
    return html`
      <style include="polar-simple-style">
        :host {
			@apply --layout-vertical;
        }
      </style>
      <slot></slot>
    `;
  }

  static get properties() {
    return {
    };
  }
}

window.customElements.define('polar-col', PolarCol);
