window.BeruangLoader = window.BeruangLoader ||
(function() {
	return {
		_toAbsoluteURL: function(arr) {
			for(let i=0;i<arr.length;i++) {
				arr[i] = this.toAbsoluteURL(arr[i]);
			}
		},

		toAbsoluteURL: function(url) {
			this._a = this._a || document.createElement("a");
			this._a.href = url;
			return this._a.href;
		},

		importModule: function(url, callback) {
			let arr = url instanceof Array ? url : [url];
			this._toAbsoluteURL(arr);
			window.define(arr, callback);
		},

		importMultiModule : function (urlarr, callback) {
			this._toAbsoluteURL(urlarr);
			window.define(urlarr, callback);
		}
	};
}());
